import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_KEY = '89a6f12e3d63c00fd3e7d7e6ba26bdeb';

function App() {
  const [city, setCity] = useState('');
  // const [country, setCountry] = useState('');
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    if (city ) {
      axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city},&appid=${API_KEY}&units=metric`)
        .then(response => {
          const data = response.data;
          setWeatherData({
            name: data.name,
            country: data.sys.country,
            temperature: data.main.temp,
            feelsLike: data.main.feels_like
          });
        })
        .catch(error => {
          console.log(error);
          setWeatherData(null);
        });
    }
  }, [city]);

  const handleCityChange = (event) => {
    setCity(event.target.value);
  };

  // const handleCountryChange = (event) => {
  //   setCountry(event.target.value);
  // };

  return (
    <div>
      <h1 className='title'>Weather App</h1>
      <input type="text" placeholder="City" onChange={handleCityChange} />
      {/* <input type="text" placeholder="Country" onChange={handleCountryChange} /> */}
      {weatherData ?
        <div className='botom'>
          <h2>{weatherData.name}, {weatherData.country}</h2>
          <h3>Temperature: {weatherData.temperature} °C</h3>
          <h3>Feels like: {weatherData.feelsLike} °C</h3>
        </div> :
        <p>Please enter a valid city</p>
      }
    </div>
  );
}

export default App;
