import React, { useState } from 'react';
import axios from 'axios';
import { FaSpinner } from 'react-icons/fa';
import { IconContext } from 'react-icons';
import { WiDaySunny, WiNightClear, WiCloud, WiDayRain, WiNightRain, WiThunderstorm, WiSnow } from 'react-icons/wi';
import './App.css';

const API_KEY = '89a6f12e3d63c00fd3e7d7e6ba26bdeb';

const App = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage(null);
    try {
      const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
      setWeatherData(response.data);
      setCity('');
    } catch (error) {
      console.log(error);
      setErrorMessage('An error occurred while fetching weather data.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setCity(e.target.value);
  };

  const getWeatherIcon = (weatherCode, isDay) => {
    switch (weatherCode) {
      case 800:
        return isDay ? <WiDaySunny /> : <WiNightClear />;
      case 801:
      case 802:
      case 803:
        return <WiCloud />;
      case 500:
      case 501:
      case 502:
      case 503:
      case 504:
      case 511:
      case 520:
      case 521:
      case 522:
      case 531:
        return isDay ? <WiDayRain /> : <WiNightRain />;
      case 200:
      case 201:
      case 202:
      case 210:
      case 211:
      case 212:
      case 221:
      case 230:
      case 231:
      case 232:
      case 781:
        return <WiThunderstorm />;
      case 600:
      case 601:
      case 602:
      case 611:
      case 612:
      case 615:
      case 616:
      case 620:
      case 621:
      case 622:
        return <WiSnow />;
      default:
        return null;
    }
  };

  const getBackgroundColor = () => {
    if (!weatherData) return '';
    const sunriseTime = new Date(weatherData.sys.sunrise * 1000);
    const sunsetTime = new Date(weatherData.sys.sunset * 1000);
    const currentTime = new Date();
    return currentTime >= sunriseTime && currentTime < sunsetTime ? 'background-day' : 'background-night';
  };

  return (
    <div className={`weather-app ${getBackgroundColor()}`}>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Enter city name" value={city} onChange={handleInputChange} />
        <button type="submit" disabled={isLoading}>
          {isLoading ? <FaSpinner className="spinner" /> : 'Search'}
        </button>
      </form>
      <div className="weather-details">
      <h2>{weatherData.name}, {weatherData.sys.country}</h2>
      <div className="weather-icon">
        <IconContext.Provider value={{ className: 'weather-status-icon' }}>
          {getWeatherIcon(weatherData.weather[0].id, getBackgroundColor() === 'background-day')}
        </IconContext.Provider>
        <p>{weatherData.weather[0].description}</p>
      </div>
      <p className="temperature">{Math.round(weatherData.main.temp)}&deg;C</p>
      <p>Feels like {Math.round(weatherData.main.feels_like)}&deg;C</p>
      <p>Humidity: {weatherData.main.humidity}%</p>
    </div>
        )
</div>
);
};

export default App;
